import './App.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import React, { useState } from 'react'
import NavBar from './components/NavBar';
import News from './components/News';
import LoadingBar from 'react-top-loading-bar';

const App =()=> {
  const [progress,setProgress]=useState(0)
  // setProgress = (progress) => {
  //   this.setState({ progress: progress })
  // }
    return (

      <div>
        <Router>
          <LoadingBar height={3} color='#87CEEB' progress={progress} />
          <NavBar />
          <Routes>
            <Route exact path="/" element={<News setProgress={setProgress} key="general" pageSize={7} country="in" category="general" />} />
            <Route exact path="/business" element={<News  setProgress={setProgress} key="business" pageSize={7} country="in" category="business" />} />
            <Route exact path="/entertainment" element={<News  setProgress={setProgress} key="entertainment" pageSize={7} country="in" category="entertainment" />} />
            <Route exact path="/general" element={<News  setProgress={setProgress} key="general" pageSize={7} country="in" category="general" />} />
            <Route exact path="/health" element={<News  setProgress={setProgress} key="health" pageSize={7} country="in" category="health" />} />
            <Route exact path="/science" element={<News  setProgress={setProgress} key="science" pageSize={7} country="in" category="science" />} />
            <Route exact path="/sports" element={<News  setProgress={setProgress} key="sports" pageSize={7} country="in" category="sports" />} />
            <Route exact path="/technology" element={<News  setProgress={setProgress} key="technology" pageSize={7} country="in" category="technology" />} />
          </Routes>
        </Router>
      </div>

    )

}

export default App
