import React, { useEffect, useState } from 'react'
import NewsItem from './NewItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";
const News = (props) => {
	// constructor(props) {
    //     super(props);
    //     this.state = {
    //         articles: [],
    //         loading: true,
    //         page: 1,
    //         totalResults: 0
    //     }
    //     document.title = `${this.capitalizeFirstLetter(this.props.category)} - NewsMonkey`;
    // }
	const [articles,setArticles]=useState([])
	const [loading,setLoading]=useState(true)
	const [page,setPage]=useState(1)
	const [totalResults,setTotalResults]=useState(0)
	const updateNews = async () => {
		// this.props.setProgress(10);
		props.setProgress(10);
		const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apikey=key&page=${page}&pagesize=${props.pageSize}`;
		setLoading(true)
		let data = await fetch(url);
		props.setProgress(30);
		let parsedData = await data.json();
		props.setProgress(70);
		setArticles(parsedData.articles)
		setTotalResults(parsedData.totalResults)
		setLoading(false)
		props.setProgress(100);
	}
	// async componentDidMount() {
	// 	this.updateNews();
	// }
	// handlePrevClk = async () => {
	// 	this.setState({ page: this.state.page - 1 })
	// 	this.updateNews();
	// }
	// handleNextClk = async () => {
	// 	this.setState({ page: this.state.page + 1 })
	// 	this.updateNews();
	// }
	useEffect(()=>{
		updateNews();
	document.title=`${capiFirstL(props.category)}-ParrotSays`
	},[])
	const fetchMoreData = async () => {
		// this.setState({ page: this.state.page + 1 })
		let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apikey=key&page=${page+1}&pagesize=${props.pageSize}`;
		setPage(page+1)
		let data = await fetch(url);
		let parsedData = await data.json();
		setArticles(articles.concat(parsedData.articles))
		setTotalResults(parsedData.totalResults)
	}
	const capiFirstL = (string) => {
		return string.charAt(0).toUpperCase() + string.slice(1);
	}

	return (
		<>
			<h3 className='text-center'>ParrotSays-Top {props.category} Headlines</h3>
			{loading && <Spinner />}
			<InfiniteScroll dataLength={articles.length}
				next={fetchMoreData}
				hasMore={articles.length !== totalResults}
				loader={<Spinner />}>
				<div className="container">
					<div className="row">
						{!loading && articles.map((ele) => {
							return <div className="col-md-4" ><NewsItem title={ele.title ? ele.title.slice(0, 45) : "Headline not found"} description={ele.description ? ele.description.slice(0, 88) : "No description found"} imgUrl={ele.urlToImage} newsUrl={ele.url} date={ele.publishedAt} author={ele.author} source={ele.source.name} /></div>
						})}
					</div>
				</div>
			</InfiniteScroll>
			{/* <div className="container d-flex justify-content-between">
					<button type="button" className="btn btn-dark" disabled={this.state.page <= 1} onClick={this.handlePrevClk}>&#8592;Previous</button>
					<button type="button" className="btn btn-dark" disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / 20)} onClick={this.handleNextClk}>Next&rarr;</button>
				</div> */}
		</>
	)
}
News.defaultProps = {
	country: 'in',
	pageSize: '10',
	category: 'general'
}
News.propTypes = {
	country: PropTypes.string,
	pageSize: PropTypes.number,
	category: PropTypes.string

}
export default News
